import React from 'react'
import {  useEffect } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { getPopulares} from '../actions'

export default function Dropdown() {
    const dispatch = useDispatch();
    const populares = useSelector((state => state.populares));
    const primerasPopulares = populares.results && populares.results.slice(0,4);
    
    useEffect (() =>{                              
        dispatch(getPopulares());                                                 
        
    }, [dispatch]);
  return (
    <div>
        <h1>POPULARES</h1>
        {primerasPopulares && 
            <div>
                {primerasPopulares.map(el => 
                <div key={el.title}>
                    <img src={'https://image.tmdb.org/t/p/w200' + el.backdrop_path} alt='' key={el.id}/>
                    <h2 key={el.original_title}>{el.original_title}</h2>
                </div>)}
            </div>}
        
        {/* <h1>MIS PELICULAS</h1>
        {lista ? lista.map(el => 
            <div key={el.title}>
                <img src={el.img} key={el.img} alt='' width='200px' height='125px'></img>
                <h3 key={el.title}>{el.title}</h3>
            </div>) : <h3 key='no data'>No Data</h3>} */}
    </div>

  )
}
