import React from 'react'
import { /*useState,*/ useEffect } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import './Home.css'
import { getPopulares } from '../actions'









export default function Home() {
    const dispatch = useDispatch();
    const populares = useSelector((state => state.populares));
    const primerasPopulares = populares.results.slice(0, 4)


    useEffect (() =>{                              
        dispatch(getPopulares());                         
    }, [dispatch]);


    console.log('POPULARES: ', populares);
    console.log('PRIMERAS: ', primerasPopulares);

  return (
    <div>
        <h1>HOME</h1>
        
        <h1>POPULARES</h1>
        {populares.results ? <div>{populares.results.map(el => <img src={'https://image.tmdb.org/t/p/w200' + el.poster_path} alt='' key={el.id}/>)}</div> : <h2>error</h2>}
        <h2>{populares.results.map(el => el.original_title)}</h2>
       
    </div>
  )
}
