import React from 'react';
import { useState} from 'react';
import './Modal.css';

export default function Modal() {
    
    let getStorage = JSON.parse(localStorage.getItem('ListaStorage'));
    const [lista, setLista] = useState(getStorage ? getStorage : [] );
    const [inputText, setInputText] = useState('');
    const [imgPreview, setImgPreview] = useState(null);

  function handleInputChange(e){      //Toma el valor del input
    e.preventDefault()
    setInputText(e.target.value)
}

function handleImgInputChange(e){
    const reader = new FileReader();
    reader.readAsDataURL(e.target.files[0])
    reader.onload = (e) => {
        e.preventDefault()
        setImgPreview(e.target.result)
        
    }

}

function handleAgregar(e){                                          //Boton Agregar
    e.preventDefault();
    setLista([...lista, {
        
        img: imgPreview,
        title: inputText                              //agrega el valor del input a la lista
    }]); 
    
    setInputText('');
    setImgPreview(null);
    alert('Película Agregada!');
         

}

localStorage.setItem('ListaStorage', JSON.stringify(lista));    //GUARDA LA LISTA EN EL LOCAL STORAGE 
localStorage.setItem('Input', JSON.stringify(inputText));   



  return (
      <div className='modal-back'>
    <div className='modal-container'>
      <p className='modal-title'>AGREGAR PELICULA</p>
        
        <form className='form-modal' onSubmit={(e) => handleAgregar(e)}>
          <label className='label'>
            <input type='file' accept='image' onChange={(e) => handleImgInputChange(e)}></input>
            <span>Agregá un archivo o arrastralo y solstalo aquí</span>
          </label>
          <div>
            <input className='modal-nombre' type="text" placeholder='Título' onChange={ (e) => handleInputChange(e) }></input>
            <hr></hr>
          </div> 
            <button className='modal-btn' type='submit'>subir película</button>
        </form>
    </div>
    </div>
  )
}
