import React from 'react'
import { useEffect } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { getDestacada} from '../actions'
import './Home.css'
import NavBar from './NavBar';
import Dropdown from './Dropdown';
import Modal from './Modal';


export default function Home() {
    const dispatch = useDispatch();
    const destacadas = useSelector((state => state.destacada));
    const modal = useSelector((state => state.modal));
    const peliculaDestacada = destacadas.results && destacadas.results[0];
    const img = peliculaDestacada && 'https://image.tmdb.org/t/p/original' + peliculaDestacada.backdrop_path;
    
    
    useEffect (() =>{                                                      
        dispatch(getDestacada());
                                  
    }, [dispatch]);
    
    
    
    return (
        <div style={{backgroundImage: `url(${img})`, backgroundRepeat: 'no-repeat', backgroundSize: 'cover', objectFit: 'cover', position: 'fixed', width: '100%', height: '100%'}}>
          <NavBar/>
          <div className='original-container'>
            <h4 className='home-original'>original de</h4>
            <h4 className='original-liteflix'>liteflix</h4>
          </div>
          <div>
            {peliculaDestacada && <h2 className='destacada-title'>{peliculaDestacada.original_title}</h2>}
            <button className='reproducir-btn'>Reproducir</button>
            <button className='lista-btn'>Mi Lista</button>
            <Dropdown/>
          </div>

        {modal && <Modal/>}
  
    </div>
  )
}
