import React from 'react'
import { useState, useEffect } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { getDestacada} from '../actions'
import './Home.css'
import Dropdown from './Dropdown';


export default function Home() {
    const dispatch = useDispatch();
    const destacadas = useSelector((state => state.populares));
    const peliculaDestacada = destacadas.results && destacadas.results[5];
    let getStorage = JSON.parse(localStorage.getItem('ListaStorage'));
    const [lista, setLista] = useState(getStorage ? getStorage : [] );
    const [inputText, setInputText] = useState('');
    const [imgPreview, setImgPreview] = useState(null);
    
    
    useEffect (() =>{                                                      
        dispatch(getDestacada());                           
    }, [dispatch]);
    
    // useEffect (() => {
        
    //     if(getStorage !== null){
    //         setLista(getStorage)        //setea la lista con la data del localStorage
    //     }
    //     // eslint-disable-next-line
    // }, [])
    
    function handleInputChange(e){      //Toma el valor del input
        e.preventDefault()
        setInputText(e.target.value)
    }

    function handleImgInputChange(e){
        const reader = new FileReader();
        reader.readAsDataURL(e.target.files[0])
        reader.onload = (e) => {
            e.preventDefault()
            setImgPreview(e.target.result)
            
        }

    }
    
    function handleAgregar(e){                                          //Boton Agregar
        e.preventDefault();
        setLista([...lista, {
            
            img: imgPreview,
            title: inputText                              //agrega el valor del input a la lista
        }]); 
        
        setInputText('');
        setImgPreview(null)    
    }
    
    localStorage.setItem('ListaStorage', JSON.stringify(lista));    //GUARDA LA LISTA EN EL LOCAL STORAGE 
    localStorage.setItem('Input', JSON.stringify(inputText));       
    
    
    
    return (
        <div key='main-div'>
        <h1>HOME</h1>

        <h1>PELICULA DESTACADA</h1>
        {peliculaDestacada && 
            <div> 
                <img src={'https://image.tmdb.org/t/p/w200' + peliculaDestacada.backdrop_path} alt='' key={peliculaDestacada.id}/>
                <h2>{peliculaDestacada.original_title}</h2>
            </div>}


        <Dropdown/>

        {/* <h1>POPULARES</h1>
        {primerasPopulares && 
            <div>
                {primerasPopulares.map(el => 
                <div key={el.title}>
                    <img src={'https://image.tmdb.org/t/p/w200' + el.backdrop_path} alt='' key={el.id}/>
                    <h2 key={el.original_title}>{el.original_title}</h2>
                </div>)}
            </div>} */}
        
        {/* <h1>MIS PELICULAS</h1>
        {lista ? lista.map(el => 
            <div key={el.title}>
                <img src={el.img} key={el.img} alt='' width='200px' height='125px'></img>
                <h3 key={el.title}>{el.title}</h3>
            </div>) : <h3 key='no data'>No Data</h3>} */}
       

        <h1>AGREGAR PELICULA</h1>
        
        <form className='form-home' onSubmit={(e) => handleAgregar(e)}>
            <input type='file' accept='image' onChange={(e) => handleImgInputChange(e)}></input>
            <input type="text" onChange={ (e) => handleInputChange(e) }></input>
            <button type='submit'>Agregar</button>
        </form>

       
    </div>
  )
}
