import React from 'react'
import { useState, useEffect } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import './Home.css'
import { getDestacada, getPopulares } from '../actions'









export default function Home() {
    const dispatch = useDispatch();
    const populares = useSelector((state => state.populares));
    const destacadas = useSelector((state => state.populares));
    const peliculaDestacada = destacadas.results && destacadas.results[9]
    const primerasPopulares = populares.results && populares.results.slice(0,4);
    const [inputText, setInputText] = useState('');
    const [nombre, setNombre] = useState('');
    const [savedData, setSavedData] = useState(false)
    


    useEffect (() =>{                              
        dispatch(getPopulares());                         
        dispatch(getDestacada());
        setNombre(getData());                         
    }, [dispatch]);

    function handleInputChange(e){
        setInputText(e.target.value)
        console.log('INPUT: ', inputText);

        if(e.target.value === ''){
            setSavedData(false)
        }
        
    }

    function saveData(){
        localStorage.setItem('nombre', inputText)
        setSavedData(true)
    }

    function getData(){
        return localStorage.getItem('nombre');
    }


    //console.log('POPULARES: ', populares);
    //console.log('PRIMERAS: ', primerasPopulares);
    //console.log('DESTACADA: ', peliculaDestacada);
    

  return (
    <div>
        <h1>HOME</h1>

        <h1>PELICULA DESTACADA</h1>
        {peliculaDestacada && <div> <img src={'https://image.tmdb.org/t/p/w200' + peliculaDestacada.poster_path} alt='' key={peliculaDestacada.id}/></div>}
        {peliculaDestacada && <h2>{peliculaDestacada.original_title}</h2>}

        
        <h1>POPULARES</h1>
        {primerasPopulares && <div>{primerasPopulares.map(el => <img src={'https://image.tmdb.org/t/p/w200' + el.poster_path} alt='' key={el.id}/>)}</div>}
        {primerasPopulares && <h2>{primerasPopulares.map(el => el.original_title)}</h2>}
        
        <h1>MIS PELICULAS</h1>
        <h2>{!!savedData && nombre}</h2>

        <h1>AGREGAR PELICULA</h1>
        {/* <input type="file" accept='image' onChange={ handleInputChange }></input> */}
        <input type="text" onChange={ handleInputChange }></input>
        <button onClick={ saveData }>Agregar</button>

       
    </div>
  )
}
