import React from 'react'
import { useState, useEffect } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import './Home.css'
import { getDestacada, getPopulares} from '../actions'


export default function Home() {
    const dispatch = useDispatch();
    const populares = useSelector((state => state.populares));
    const destacadas = useSelector((state => state.populares));
    const peliculaDestacada = destacadas.results && destacadas.results[5]
    const primerasPopulares = populares.results && populares.results.slice(0,4);
    let getStorage = JSON.parse(localStorage.getItem('ListaStorage'));
    const [inputText, setInputText] = useState('');
    const [lista, setLista] = useState(getStorage ? getStorage : [] )
    const [imgPreview, setImgPreview] = useState(null);
    
    
    useEffect (() =>{                              
        dispatch(getPopulares());                         
        dispatch(getDestacada());                        
        
    }, [dispatch]);
    
    useEffect (() => {
        
        if(getStorage !== null){
            setLista(getStorage)        //setea la lista con la data del localStorage
        }
        // eslint-disable-next-line
    }, [])
    
    function handleInputChange(e){      //Toma el valor del input
        e.preventDefault()
        setInputText(e.target.value)
    }

    function handleImgInputChange(e){
        const reader = new FileReader();
        reader.readAsDataURL(e.target.files[0])
        reader.onload = (e) => {
            e.preventDefault()
            setImgPreview(e.target.result)
            
        }

    }
    
    function handleAgregar(e){                                          //Boton Agregar
        e.preventDefault();
        setLista([...lista, {
            
            img: imgPreview,
            title: inputText                              //agrega el valor del input a la lista
        }]); 
        
        setInputText('');
        setImgPreview(null)    
    }
    
    localStorage.setItem('ListaStorage', JSON.stringify(lista));    //GUARDA LA LISTA EN EL LOCAL STORAGE 
    localStorage.setItem('Input', JSON.stringify(inputText));       
    
    
    
    return (
        <div>
        <h1>HOME</h1>

        <h1>PELICULA DESTACADA</h1>
        {peliculaDestacada && <div> <img src={'https://image.tmdb.org/t/p/w200' + peliculaDestacada.backdrop_path} alt='' key={peliculaDestacada.id}/></div>}
        {peliculaDestacada && <h2>{peliculaDestacada.original_title}</h2>}

        
        <h1>POPULARES</h1>
        {primerasPopulares && <div>{primerasPopulares.map(el => <img src={'https://image.tmdb.org/t/p/w200' + el.backdrop_path} alt='' key={el.id}/>)}</div>}
        {primerasPopulares && <h2>{primerasPopulares.map(el => el.original_title)}</h2>}
        
        <h1>MIS PELICULAS</h1>
        {lista ? lista.map(el => 
            <div>
                <img src={el.img} key={el.title} alt='' width='200px' height='125px'></img>
                <h3 key={el.title}>{el.title}</h3>
            </div>) : <h3>No Data</h3>}
       

        <h1>AGREGAR PELICULA</h1>
        
        <form className='form-home' onSubmit={(e) => handleAgregar(e)}>
            <input type='file' accept='image' onChange={(e) => handleImgInputChange(e)}></input>
            <input type="text" onChange={ (e) => handleInputChange(e) }></input>
            <button type='submit'>Agregar</button>
        </form>

       
    </div>
  )
}
