const initialState = {
    destacada: [],
    populares: [],
    misPeliculas: []
};

function rootReducer(state = initialState, action) {

    switch(action.type) {
        
        case "GET_DESTACADA":
            return{
                ...state,                               
                destacada: action.payload,               
            
            };

        case "GET_POPULARES":
            return{
                ...state,                               
                populares: action.payload,               
            
            };

        case "POST_PELICULA":
            return{
                ...state,                               
                misPeliculas: JSON.parse(localStorage.getItem('Lista')),

            };
        
    default:
            return state;
}

}

export default rootReducer;
