import React from 'react'
import { useEffect, useState } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { getPopulares} from '../actions'
import './Dropdown.css'
import Card from './Card.jsx'


export default function Dropdown() {
    const dispatch = useDispatch();
    const populares = useSelector((state => state.populares));
    const primerasPopulares = populares.results && populares.results.slice(0,4);
    let getStorage = JSON.parse(localStorage.getItem('ListaStorage'));
    const [lista, setLista] = useState(getStorage ? getStorage : [] );
    const [cambioSelect, setCambioSelect] = useState(false)
    
    useEffect (() =>{                              
        dispatch(getPopulares());                                                 
        
    }, [dispatch]);

    useEffect (() => {
        
        if(getStorage !== null){
            setLista(getStorage)        //setea la lista con la data del localStorage
        }
        // eslint-disable-next-line
    }, [])

    function handleSelect(e){
        e.preventDefault();

        if(cambioSelect === false){
            
            setCambioSelect(true)
        }else{
            setCambioSelect(false)
        }
    }


  return (
    <div className='drop-container'>
        <div className='drop-select'>
            <p>Ver:</p>
        <select className='drop-options' value='default' onChange={ e => handleSelect(e)}>
            <option className='drop-option' value='populares' >Populares</option>
            <option className='drop-option' value='mis-peliculas'>Mis Peliculas</option>
        </select>

        </div>

        {!cambioSelect? 
            primerasPopulares && 
                <div>
                    {primerasPopulares.map(el => { 
                        return(
                            <Card
                                key={el.title}
                                title={el.original_title}
                                image={'https://image.tmdb.org/t/p/original' + el.backdrop_path}
                            />
                    )})}
                </div> :
        
        
            lista && 
              <div>
                {lista.map(el => {
                    return(
                        <Card
                        key={el.title}
                        title={el.title}
                        image={el.img}

                        />

                    )})}
                </div> }
                 {/* <div key={el.title}>
                     <img src={el.img} key={el.img} alt='' width='200px' height='125px'></img>
                     <p key={el.title}>{el.title}</p>
                 </div>)} */}
        
    </div>

  )
}
