import React from 'react'
import { useState, useEffect } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import './Home.css'
import { getDestacada, getPopulares } from '../actions'










export default function Home() {
    const dispatch = useDispatch();
    const populares = useSelector((state => state.populares));
    const destacadas = useSelector((state => state.populares));
    //const misPeliculas = useSelector((state => state.misPeliculas));
    const peliculaDestacada = destacadas.results && destacadas.results[9]
    const primerasPopulares = populares.results && populares.results.slice(0,4);
    const [inputText, setInputText] = useState('');
    const [lista, setLista] = useState(['Hulk', 'Batman'])
    let getStorage = JSON.parse(localStorage.getItem('ListaStorage'));
    
    useEffect (() =>{                              
        dispatch(getPopulares());                         
        dispatch(getDestacada());                        
                               
    }, [dispatch]);
    

    function handleInputChange(e){      //Toma el valor del input
        e.preventDefault()
        setInputText(e.target.value)
    }
    
    
    useEffect (() => {
        
        if(getStorage !== null){
            setLista(getStorage)        //setea la lista con la data del localStorage
        }
        // eslint-disable-next-line
    }, [])
    
    function handleAgregar(e){                                          //Boton Agregar
        e.preventDefault();
        setLista(lista.concat(inputText));                              //agrega el valor del input a la lista
        localStorage.setItem('ListaStorage', JSON.stringify(lista));    //GUARDA LA LISTA EN EL LOCAL STORAGE 
        localStorage.setItem('Input', JSON.stringify(inputText));       
        setInputText('')
        
        
    }

    const prueba = ['Hola', 'Chau']

    localStorage.setItem('Prueba', JSON.stringify(prueba))
    console.log(JSON.parse(localStorage.getItem('Prueba')));
   //console.log('LISTA: ', lista);
    //console.log('LISTASTORAGE: ', JSON.parse(localStorage.getItem('ListaStorage')));
    
    
  return (
    <div>
        <h1>HOME</h1>

        <h1>PELICULA DESTACADA</h1>
        {peliculaDestacada && <div> <img src={'https://image.tmdb.org/t/p/w200' + peliculaDestacada.poster_path} alt='' key={peliculaDestacada.id}/></div>}
        {peliculaDestacada && <h2>{peliculaDestacada.original_title}</h2>}

        
        <h1>POPULARES</h1>
        {primerasPopulares && <div>{primerasPopulares.map(el => <img src={'https://image.tmdb.org/t/p/w200' + el.poster_path} alt='' key={el.id}/>)}</div>}
        {primerasPopulares && <h2>{primerasPopulares.map(el => el.original_title)}</h2>}
        
        <h1>MIS PELICULAS</h1>
        {lista ? lista.map(el => <h3 key={el}>{el}</h3>) : <h3>No Data</h3>}
       

        <h1>AGREGAR PELICULA</h1>
        {/* <input type="file" accept='image' onChange={ handleInputChange }></input> */}
        <form onSubmit={(e) => handleAgregar(e)}>
            <input type="text" onChange={ (e) => handleInputChange(e) }></input>
            <button type='submit'>Agregar</button>
        </form>

       
    </div>
  )
}
