import React from 'react'
import { useEffect, useState } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { getPopulares} from '../actions'
import './Dropdown.css'


export default function Dropdown() {
    const dispatch = useDispatch();
    const populares = useSelector((state => state.populares));
    const primerasPopulares = populares.results && populares.results.slice(0,4);
    let getStorage = JSON.parse(localStorage.getItem('ListaStorage'));
    const [lista, setLista] = useState(getStorage ? getStorage : [] );
    
    useEffect (() =>{                              
        dispatch(getPopulares());                                                 
        
    }, [dispatch]);

    useEffect (() => {
        
        if(getStorage !== null){
            setLista(getStorage)        //setea la lista con la data del localStorage
        }
        // eslint-disable-next-line
    }, [])


  return (
    <div className='drop-container'>
        <p>POPULARES</p>
        {primerasPopulares && 
            <div>
                {primerasPopulares.map(el => 
                <div key={el.title} className='pop-card'>
                    <img src={'https://image.tmdb.org/t/p/original' + el.backdrop_path} alt='' key={el.id}/>
                    <p  className='drop-title' key={el.original_title}>{el.original_title}</p>
                </div>)}
            </div>}
        
        <p>MIS PELICULAS</p>
        {lista ? lista.map(el => 
            <div key={el.title}>
                <img src={el.img} key={el.img} alt='' width='200px' height='125px'></img>
                <p key={el.title}>{el.title}</p>
            </div>) : <p key='no data'>No Data</p>}
    </div>

  )
}
